};

export type TestTypes = 'Unit' | 'Integration' | 'PostDeployment';

export const testGroupTitle = (testType: TestTypes, groupName: string) =>
    `[${testType.toUpperCase()} Tests] ${groupName}`;

export const formatTestTitle = (title: string | Function) =>
    typeof title === 'function' ? `#${title.name}` : title;

import { LogEntry, LogSeverity, write } from 'firebase-functions/lib/logger';
import { Log, Scope } from './logger.types';
import { isObject } from 'lodash';
import { inspect } from 'util';
import { get } from 'stack-trace';

export let logGlobalContext: { [key: string]: any } = {};

export function addLogGlobalContext(key: string, entry: any) {
    logGlobalContext[key] = entry;
}

/** Removes all context that were included in the logs. */
export function cleanLogGlobalContext() {
    logGlobalContext = {};
}

function checkIfObjectAndConvertToString(data: any) {
    if (isObject(data)) {
        return inspect(data);
    }

    return data;
}

function getFilePath() {
    // (code is all bundled into index, hence its the current default)
    const DEFAULT_FILEPATH = 'gcf/src/logger/index.ts';
    try {
        const testError = { stack: undefined };
        Error.captureStackTrace(testError);
        const stack: string = testError.stack ?? '';
        const file = stack.split('\n')[4];
        return file.substring(file.lastIndexOf('gcf/') + 'gcf/'.length, file.indexOf('.ts:'));
    } catch (e) {
        return DEFAULT_FILEPATH;
    }
}

function getScope(): Scope {
    const trace = get();

    const scope: Scope = {};

    // trace[0] => getScope, trace[1] -> wrapper function below
    // so we take trace[2] to get the correct scope of the caller
    const SUB_TRACE_START_INDEX = 2;
    scope.fileName = getFilePath();

    // To get the rest of the scope, we traveres down the stack and find the first named
    // function. This will allow us to handle scoping of anonymous functions/callbacks.
    for (let i = SUB_TRACE_START_INDEX; i < trace.length; i += 1) {
        const subTrace = trace[i];
        scope.functionName = subTrace?.getFunctionName();
        scope.methodName = subTrace?.getMethodName();

        if (scope.functionName) {
            break;
        }
    }

    return scope;
}

export function makeLogger(severity: LogSeverity): Log {
    return (message, additionalProperties) => {
        let messageString;

        // We want to accept all falsy values except undefined
        if (message !== undefined) {
            let messageArray = [message];
            if (Array.isArray(message)) {
                messageArray = message;
            }
            messageString = messageArray.map(checkIfObjectAndConvertToString).join(' ');
        }

        const logEntry: LogEntry = {
            message: messageString,
            severity,
            ...logGlobalContext,
            scope: {
                ...getScope(),
            },
        };

        if (additionalProperties) {
            const { namespace, properties } = additionalProperties;
            // Handle instances where user throws in a random object as type "any"
            if (!namespace || !properties) {
                logEntry.message = [
                    logEntry.message,
                    checkIfObjectAndConvertToString(additionalProperties),
                ].join(' ');
            } else {
                logEntry[namespace] = properties;
            }
        }

        write(logEntry);
    };
}

/**
 * Logger providing granular serverity levels as documented: https://cloud.google.com/logging/docs/reference/v2/rest/v2/LogEntry#logseverity
 *
 * LEVEL debug: Temporary logs for debugging, to be removed when feature is stable (should be used with a TODO {AUTHOR NAME} above it)
 * LEVEL info: Routine information, such as ongoing status or performance.
 * LEVEL metrics: Use to log metrics
 * LEVEL warn: Non-critical errors that should be filtered out in alarms (for example rateLimiter)
 * LEVEL error: Error events are likely to cause problems.
 * LEVEL critical: Critical events cause more severe problems or outages.
 *
 */
export const logger = {
    debug: makeLogger('DEBUG'),
    info: makeLogger('INFO'),
    metrics: makeLogger('NOTICE'),
    warn: makeLogger('WARNING'),
    error: makeLogger('ERROR'),
    critical: makeLogger('CRITICAL'),
};

import { AxiosRequestConfig, AxiosResponse } from 'axios';

export class ApiRequestResponseError extends Error {
    public response: AxiosResponse;
    constructor(response: AxiosResponse) {
        const { status } = response;
        super(`Response received of status:${status}`);
        this.response = response;
        this.name = 'ApiRequestResponseError';
    }
}

export class ApiRequestNoResponseError extends Error {
    public request: AxiosRequestConfig;
    constructor(request: AxiosRequestConfig) {
        super('No response received');
        this.name = 'ApiRequestNoResponseError';
        this.request = request;
    }
}

export class ApiRequestSetupError extends Error {
    constructor(message: string) {
        super(message);
        this.name = 'ApiRequestSetupError';
    }
}
export class ApiRequestResponseValidationError extends Error {
    public response: AxiosResponse;
    constructor(response: AxiosResponse) {
        const { data } = response;
        super(`Response data structure failed the validation ${JSON.stringify(data)}`);
        this.response = response;
        this.name = 'ApiRequestResponseValidationError';
    }
}

import http from 'http';
import https from 'https';
import axios, { AxiosError, AxiosResponse } from 'axios';
import { logger } from '@root/logger';
import { retry } from '@root/utils/retry';
import {
    AsyncHeaders,
    Bail,
    RequestConfig,
    RequestConfigWithSuccessHandler,
    SkipRetryPredicate,
} from './request.types';
import { createLoggerWithRequestContext } from './request.helper';
import {
    ApiRequestNoResponseError,
    ApiRequestResponseError,
    ApiRequestResponseValidationError,
    ApiRequestSetupError,
} from './request.errors';

const httpAgent = new http.Agent({ keepAlive: true });
const httpsAgent = new https.Agent({ keepAlive: true });

const DEFAULT_TIMEOUT = 6000;
const DEFAULT_RETRY = 3;

/**
 * Creates a base request client
 * @param baseUrl Base url where all other request's url will be based off from.
 * @param asyncHeaders A callback that asynchronously configures the headers.
 * @returns {request} All request will have the above configurations as default.
 */
export const makeRequestClient = (baseUrl?: string, asyncHeaders?: AsyncHeaders) => {
    // Create axios instance and assign httpAgent to persist connections
    const axiosClient = axios.create({
        baseURL: baseUrl,
        httpAgent,
        httpsAgent,
        headers: {
            'Content-Type': 'application/json',
        },
    });

    /**
     * Makes a http request that logs request states, timeouts request, and retries (default to 3 retries,
     * client errors are also retried) on failure. Response can be optionally validated or transformed.
     * @param config see https://axios-http.com/docs/req_config for more info
     * @returns T|U where T is response and U is the transformed response if onSuccessResponse is configured.
     * @throws {ApiRequestResponseError} Response was received with status that falls out of the 200-299 range.
     * @throws {ApiRequestNoResponseError} Request was sent out but no response was received.
     * @throws {ApiRequestSetupError} Request did not even make it out, failed during the setup.
     * @throws {ApiRequestResponseValidationError} Response was received but failed validation
     */
    function requestWithRetry<T>(config: RequestConfig<T>): Promise<T>; //eslint-disable-line @typescript-eslint/no-explicit-any
    function requestWithRetry<T, U>(config: RequestConfigWithSuccessHandler<T, U>): Promise<U>; //eslint-disable-line @typescript-eslint/no-explicit-any
    function requestWithRetry<T, U>( //eslint-disable-line @typescript-eslint/no-explicit-any
        config: RequestConfig<T> | RequestConfigWithSuccessHandler<T, U>
    ) {
        const loggerWithRequestContext = createLoggerWithRequestContext(
            logger,
            { baseUrl, url: config.url, method: config.method },
            config.onUpdateLogContext
        );

        const { updateContext, resetContext, updateTimeElapse, loggerWithContext } =
            loggerWithRequestContext;

        async function request(bail: Bail) {
            resetContext();
            loggerWithContext.info();

            let response: AxiosResponse | undefined;
            try {
                // Asynchronously assign headers on every request
                response = await axiosClient.request({
                    ...config,
                    timeout: config.timeout ?? DEFAULT_TIMEOUT,
                    headers: {
                        ...axiosClient.defaults.headers,
                        ...(asyncHeaders ? await asyncHeaders() : undefined),
                        ...config.headers,
                    },
                });
            } catch (error: any) {
                updateTimeElapse();
                handleError(loggerWithRequestContext, config.skipRetryPredicate, error, bail);
                return;
            }

            if (response) {
                const { status, statusText, data } = response;

                updateTimeElapse();
                updateContext({
                    status,
                    statusText,
                    data,
                });
                loggerWithContext.info();

                // Validate and warn if a validator is configured
                if (config.responseValidator && !config.responseValidator(data)) {
                    const responseValidationError = new ApiRequestResponseValidationError(response);
                    loggerWithContext.error(responseValidationError.message);
                    bail(responseValidationError);
                    return;
                }

                // Pass response data and execution to onSuccessResponse callback, the new return value will be dictated by it.
                if ('onSuccessResponse' in config) return config.onSuccessResponse(data, bail);

                return data;
            }
        }

        return retry(request, {
            retries: config.retryCount ?? DEFAULT_RETRY,
            onRetry: async (error, attempt) => {
                loggerWithContext.warn(`attempt ${attempt}. Failed due to error: ${error.message}`);
            },
        });
    }

    return {
        request: requestWithRetry,
    };
};

const skipClientErrorStatusCodes: SkipRetryPredicate = (statusCode) => {
    return statusCode >= 400 && statusCode <= 499;
};

function handleError(
    { loggerWithContext, updateContext }: ReturnType<typeof createLoggerWithRequestContext>,
    skipRetry = skipClientErrorStatusCodes,
    error: AxiosError,
    bail: (e: Error) => void
) {
    if (error.response) {
        const { status, statusText, data } = error.response;
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        updateContext({ statusText, status });

        const apiRequestResponseError = new ApiRequestResponseError(error.response);
        loggerWithContext.warn(apiRequestResponseError.message);

        if (skipRetry(status, data)) {
            loggerWithContext.warn(`Http status code: ${status} received, skipping retry`);
            bail(apiRequestResponseError);
            return;
        }

        throw apiRequestResponseError;
    } else if (error.request) {
        // The request was made but no response was received
        // `error.request` is an instance of http.ClientRequest in node.js
        const noResponseError = new ApiRequestNoResponseError(error.config);
        loggerWithContext.warn(noResponseError.message);
        bail(noResponseError);
    } else {
        loggerWithContext.warn(error.message);
        // Something happened in setting up the request that triggered an Error
        bail(new ApiRequestSetupError(error.message));
    }
}

import { Logger } from '@root/logger/logger.types';
import { OnUpdateLogContext, RequestContext } from './request.types';

export const buildRequestContextMessage = (context: RequestContext) => {
    const { baseUrl, method, status, statusText, timeTakenInMs: timeElapseInMs } = context;
    const url = [baseUrl, context.url].join('');
    const stringBuilder = [method, url];

    if (status && statusText) stringBuilder.push([status, statusText].join(':'));
    if (timeElapseInMs) stringBuilder.push(`${timeElapseInMs}ms`);

    return stringBuilder.join(' ');
};

export const createLoggerWithRequestContext = (
    logger: Logger,
    initialContext: RequestContext,
    onUpdateLogContext?: OnUpdateLogContext
) => {
    let startTimestamp = Date.now();
    let context: RequestContext = {};

    const updateContext = (contextToUpdate: RequestContext) => {
        const updates = onUpdateLogContext ? onUpdateLogContext(contextToUpdate) : contextToUpdate;

        context = {
            ...context,
            ...updates,
        };

        // Force data to be a json string instead, this can help log json payload to have a consistent schema
        if (context.data) {
            context.data = JSON.stringify(context.data);
        }
    };

    const resetContext = () => {
        startTimestamp = Date.now();
        context = {};
        updateContext(initialContext);
    };

    const updateTimeElapse = () => {
        const timeTakenInMs = Date.now() - startTimestamp;
        updateContext({ timeTakenInMs });
    };

    updateContext(initialContext);

    const loggerWithContext: Record<keyof typeof logger, (message?: string) => void> = {
        ...logger,
    };
    let key: keyof typeof logger;

    // Iterate and replace logger with a wrapped version
    for (key in logger) {
        const log = logger[key];
        loggerWithContext[key] = (message) => {
            log([buildRequestContextMessage(context), message], {
                namespace: 'requestContext',
                properties: context,
            });
        };
    }

    return {
        updateContext,
        resetContext,
        updateTimeElapse,
        loggerWithContext,
    };
};

import moment from 'moment-timezone';
import { AsyncReturnType } from '@root/utils/utils.types';
import { logger } from '@root/logger';

interface CacheEntry<T> {
    val: Promise<T>;
    lastRetrieved: number;
}

interface FunctionCache<T extends (...args: any) => Promise<any>> {
    cachedFunction: (...args: Parameters<T>) => Promise<AsyncReturnType<T>>;
    clearCache: () => void;
}

/**
 * Provides time-based caching logic around an arbitrary call, returning the wrapped call.
 * @param providerFunction: the expensive 'cached' call - should have all arguments pre-loaded.
 * @param ttl unit: seconds
 */
export function cacheWithExpiry<T extends (...args: any[]) => Promise<any>>(
    providerFunction: T,
    ttl = 86400
): FunctionCache<T> {
    const cachedValues = new Map<string, CacheEntry<AsyncReturnType<T>>>();

    const cachedFunction = async (...args: Parameters<T>): Promise<AsyncReturnType<T>> => {
        const unixMsNow = moment().valueOf();
        const serializedArgs = JSON.stringify(args);

        const lastRetrieved = cachedValues.get(serializedArgs)?.lastRetrieved ?? 0;
        if (lastRetrieved + ttl * 1000 <= unixMsNow) {
            // First guy making the request - allow others to rely on the same invocation
            logger.debug('Cache miss - retrieving underlying value.');
            const requestPromise = providerFunction(...args);
            cachedValues.set(serializedArgs, { val: requestPromise, lastRetrieved: unixMsNow });
        }

        // Erase cache if the cached value is an error.
        const promisedValue = cachedValues.get(serializedArgs)?.val as ReturnType<T>;
        try {
            return await promisedValue;
        } catch (error: any) {
            logger.warn('Underlying promise was rejected');
            cachedValues.delete(serializedArgs);
            return promisedValue;
        }
    };

    function clearCache() {
        cachedValues.clear();
    }

    return { cachedFunction, clearCache };
}

import { config } from '@root/config';
import { logger } from '@root/logger';
import { cacheWithExpiry } from '@root/utils/cacheWithExpiry';

type GetSecret = (key: string) => Promise<string>;

// eslint-disable-line no-restricted-imports
import { SecretManagerServiceClient } from '@google-cloud/secret-manager'; 
export type GcpSecretManagerClient = InstanceType<typeof SecretManagerServiceClient>;

let gcpSecretClient: undefined | GcpSecretManagerClient;

export const getGcpSecretManagerClient = () => {
    gcpSecretClient = gcpSecretClient || new SecretManagerServiceClient();
    return gcpSecretClient;
};

const getSecretRemotely: GetSecret = async (key) => {
    const project = config.projectId;
    logger.info(`Getting secret ${key} from project ${project}`);

    const gcpSecretClient = getGcpSecretManagerClient();
    const secretVersionPath = gcpSecretClient.secretVersionPath(project, key, 'latest');

    const [secret] = await gcpSecretClient.accessSecretVersion({
        name: secretVersionPath,
    });
    return secret.payload?.data?.toString() ?? '';
};

const { cachedFunction: getSecret, clearCache: clearSecretCache } =
    cacheWithExpiry(getSecretRemotely);

export { getSecret, clearSecretCache };

