import chai from 'chai';
import chaiExclude from 'chai-exclude';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';
import * as retry from '@root/utils/retry';
import { mockRetry } from '@root/testUtils/mockRetry';
import { Done } from 'mocha';
import { createSandbox, SinonSandbox } from 'sinon';

let sandbox: SinonSandbox;
exports.mochaHooks = {
    beforeEach(done: Done) {
        sandbox = createSandbox();
        chai.use(chaiExclude);
        chai.use(sinonChai);
        // CAUTION: Its important to load chai as promised after other chai extensions as loading it first
        // caused to deep.equal to always return true even when objects are not equal.
        // I didn't find any references regarding this particular issue but there are similar issues
        // reported. Following are couple of examples
        // https://github.com/domenic/chai-as-promised/issues/215
        // https://www.bountysource.com/teams/chai-as-promised/issues?tracker_ids=295399
        chai.use(chaiAsPromised);
        sandbox.replace(retry, 'retry', mockRetry);
        done();
    },
    afterEach() {
        sandbox.restore();
    },
};

import AsyncRetry from 'async-retry';

export const mockRetry: typeof AsyncRetry = (fn, options) => {
    let retries = options?.retries ?? 0;
    let bailError: Error | undefined;

    const bail = (error: Error) => {
        bailError = error;
        throw error;
    };

    const wrapper = async (): Promise<any> => {
        try {
            const results = await fn(bail, retries);
            return results;
        } catch (error: any) {
            if (retries && !bailError) {
                retries -= 1;
                return wrapper();
            }
            throw error;
        }
    };

    return wrapper();
};

import { LogEntry, LogSeverity, write } from 'firebase-functions/lib/logger';
import { Log, Scope } from './logger.types';
import { isObject } from 'lodash';
import { inspect } from 'util';
import { get } from 'stack-trace';

export let logGlobalContext: { [key: string]: any } = {};

export function addLogGlobalContext(key: string, entry: any) {
    logGlobalContext[key] = entry;
}

/** Removes all context that were included in the logs. */
export function cleanLogGlobalContext() {
    logGlobalContext = {};
}

function checkIfObjectAndConvertToString(data: any) {
    if (isObject(data)) {
        return inspect(data);
    }

    return data;
}

function getFilePath() {
    // (code is all bundled into index, hence its the current default)
    const DEFAULT_FILEPATH = 'gcf/src/logger/index.ts';
    try {
        const testError = { stack: undefined };
        Error.captureStackTrace(testError);
        const stack: string = testError.stack ?? '';
        const file = stack.split('\n')[4];
        return file.substring(file.lastIndexOf('gcf/') + 'gcf/'.length, file.indexOf('.ts:'));
    } catch (e) {
        return DEFAULT_FILEPATH;
    }
}

function getScope(): Scope {
    const trace = get();

    const scope: Scope = {};

    // trace[0] => getScope, trace[1] -> wrapper function below
    // so we take trace[2] to get the correct scope of the caller
    const SUB_TRACE_START_INDEX = 2;
    scope.fileName = getFilePath();

    // To get the rest of the scope, we traveres down the stack and find the first named
    // function. This will allow us to handle scoping of anonymous functions/callbacks.
    for (let i = SUB_TRACE_START_INDEX; i < trace.length; i += 1) {
        const subTrace = trace[i];
        scope.functionName = subTrace?.getFunctionName();
        scope.methodName = subTrace?.getMethodName();

        if (scope.functionName) {
            break;
        }
    }

    return scope;
}

export function makeLogger(severity: LogSeverity): Log {
    return (message, additionalProperties) => {
        let messageString;

        // We want to accept all falsy values except undefined
        if (message !== undefined) {
            let messageArray = [message];
            if (Array.isArray(message)) {
                messageArray = message;
            }
            messageString = messageArray.map(checkIfObjectAndConvertToString).join(' ');
        }

        const logEntry: LogEntry = {
            message: messageString,
            severity,
            ...logGlobalContext,
            scope: {
                ...getScope(),
            },
        };

        if (additionalProperties) {
            const { namespace, properties } = additionalProperties;
            // Handle instances where user throws in a random object as type "any"
            if (!namespace || !properties) {
                logEntry.message = [
                    logEntry.message,
                    checkIfObjectAndConvertToString(additionalProperties),
                ].join(' ');
            } else {
                logEntry[namespace] = properties;
            }
        }

        write(logEntry);
    };
}

/**
 * Logger providing granular serverity levels as documented: https://cloud.google.com/logging/docs/reference/v2/rest/v2/LogEntry#logseverity
 *
 * LEVEL debug: Temporary logs for debugging, to be removed when feature is stable (should be used with a TODO {AUTHOR NAME} above it)
 * LEVEL info: Routine information, such as ongoing status or performance.
 * LEVEL metrics: Use to log metrics
 * LEVEL warn: Non-critical errors that should be filtered out in alarms (for example rateLimiter)
 * LEVEL error: Error events are likely to cause problems.
 * LEVEL critical: Critical events cause more severe problems or outages.
 *
 */
export const logger = {
    debug: makeLogger('DEBUG'),
    info: makeLogger('INFO'),
    metrics: makeLogger('NOTICE'),
    warn: makeLogger('WARNING'),
    error: makeLogger('ERROR'),
    critical: makeLogger('CRITICAL'),
};

import { formatTestTitle, testGroupTitle } from '@root/testUtils/format';

export const describeUnitTestsFor = (
    nameOfComponentToTest: string | Function,
    fn: (this: Mocha.Suite) => void
) => {
    describe(testGroupTitle('Unit', formatTestTitle(nameOfComponentToTest)), fn);
};

export const describeIntegrationTestsFor = (
    nameOfComponentToTest: string,
    fn: (this: Mocha.Suite) => void
) => {
    describe(testGroupTitle('Integration', formatTestTitle(nameOfComponentToTest)), fn);
};

export const describePostDeploymentTestsFor = (
    nameOfComponentToTest: string,
    fn: (this: Mocha.Suite) => void
) => {
    describe(testGroupTitle('PostDeployment', formatTestTitle(nameOfComponentToTest)), fn);
};

export type TestTypes = 'Unit' | 'Integration' | 'PostDeployment';

export const testGroupTitle = (testType: TestTypes, groupName: string) =>
    `[${testType.toUpperCase()} Tests] ${groupName}`;

export const formatTestTitle = (title: string | Function) =>
    typeof title === 'function' ? `#${title.name}` : title;

import * as functions from 'firebase-functions';
import { logger } from '@root/logger';
import { HttpsError } from 'firebase-functions/lib/providers/https';
import { captureException } from '@sentry/serverless';
import { AsyncReturnType } from '@root/utils/utils.types';

export interface ErrorDetails {
    code: functions.https.FunctionsErrorCode;
    message: string;
}

export function createError<T extends Readonly<{ [errorCode: string]: ErrorDetails }>>(
    errorMap: T
) {
    return function (error: keyof T) {
        const details = error;
        const { code, message } = errorMap[error] as ErrorDetails;
        logger.warn(`Error. Code: ${code}. TT-custom code: ${details}. message: ${message}`);
        return new functions.https.HttpsError(code, message, details);
    };
}

export const GENERAL_ERRORS = {
    INTERNAL: {
        code: 'internal',
        message: 'TT backend has an unexpected problem.',
    },
    INVALID_FIELD: {
        code: 'invalid-argument',
        message: 'Request data did not meet TT backend validation',
    },
    INVALID_TTID: {
        code: 'permission-denied',
        message: 'invalid user',
    },
    RATE_LIMIT: {
        code: 'resource-exhausted',
        message: 'Per-user quota exceeded',
    },
    DENIED: {
        code: 'permission-denied',
        message: 'TT backend denies your access to this resource.',
    },
} as const;

/**
 * Light wrapper around firebase's HttpsError class - to enforce standardization of details and default logging.
 * @param error string, used as key for our enumerated errors.
 */
export const createTtError = createError(GENERAL_ERRORS);

/** Used to wrap around Cloud Functions, to ensure we only return HttpsError to clients. */
export function wrapInHttpsError<T extends (...args: any[]) => Promise<any>>(
    handler: T
): (...args: Parameters<T>) => Promise<AsyncReturnType<T>> {
    return async (...args) => {
        try {
            return await handler(...args);
        } catch (error: any) {
            let errorToThrow: Error;
            if (error instanceof HttpsError) {
                errorToThrow = error;
            } else {
                logger.error([`${handler.name} execution failed with unhandleable error: `, error]);
                captureException(error);
                errorToThrow = createTtError('INTERNAL');
            }

            throw errorToThrow;
        }
    };
}

export class PromiseTimeout extends Error {
    constructor(maxRuntimeInMs: number) {
        super(`Await of promises exceeded ${maxRuntimeInMs}ms`);
    }
}

export async function limitPromiseRuntime<T>(
    promise: Promise<T>,
    maxRuntimeInMs: number
): Promise<T> {
    return new Promise((resolve, reject) => {
        const timeout = setTimeout(async () => {
            reject(new PromiseTimeout(maxRuntimeInMs));
        }, maxRuntimeInMs + 1);

        promise
            .then((results) => {
                resolve(results);
            })
            .catch((err) => reject(err))
            .finally(() => {
                // make sure to clear timeout even if the promise rejects
                clearTimeout(timeout);
            });
    });
}
